const source = ({ contract }) => `
            account: '${contract}'
            startBlock: 0`

const mapping = ({contractName}) => `
            apiVersion: 0.0.5
            language: wasm/assemblyscript
            entities:
              - ${contractName.slice(0,3)}Transaction
            receiptHandlers:
              - handler: handleReceipt
            file: ./src/${contractName}Mapping.ts`

module.exports = {
  source,
  mapping,
}
